    @Inject
    public Ids(Random rand, SecureRandom secureRandom, Settings settings) {
        int ringSize = settings.getInt("ringSize", 10);
        // Check that we have enough primes to fill the ring with distinct values
        if (ringSize > PRIMES.length) {
            throw new IllegalArgumentException("Ring size larger than prime list: " + ringSize + " > " + PRIMES.length);
        }
        // Use a set to guarantee no duplicates
        Set<Integer> ints = new HashSet<>();
        // Loop until we have enough - don't use a counter because we could
        // get duplicates and wind up with a smaller set than desired
        while (ints.size() < ringSize) {
            ints.add(PRIMES[secureRandom.nextInt(PRIMES.length)]);
        }
        // Put them in the array
        primeSet = new int[ints.size()];
        Iterator<Integer> iter = ints.iterator();
        for (int i = 0; iter.hasNext(); i++) {
            primeSet[i] = iter.next();
        }
        this.rand = rand;
        // This will cycle through the index into the array and loop around to 0
        robin = new AtomicRoundRobin(primeSet.length);
        // Make sure we will xor at least two bits
        int x;
        do {
            x = rand.nextInt();
        } while (x < 1);
        xor = x;
    }

    public long next() {
        // Next random value
        long r = rand.nextInt();
        // Next prime - important for these to be longs or we'll do int
        // multiplication and get bad values
        long prime = primeSet[robin.next()];
        long result = r * prime;
        // Xor it with our one-time value
        result ^= xor;
        return result;
    }

    public boolean isValid(long value) {
        // Undo the xor
        long unxored = value ^ xor;
        // See if it's divisible by one of our primes
        for (int i = 0; i < primeSet.length; i++) {
            long mod = unxored % primeSet[i];
            if (mod == 0) {
                return true;
            }
        }
        return false;
    }
